

import React from 'react';
import Image from 'next/image';

const Home = () => {
  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-50 to-teal-100 text-gray-800">
      <header className="bg-gradient-to-r from-blue-500 to-teal-500 p-6 shadow-lg text-center">
        <div className="flex justify-center mb-4">
          <Image 
            src="https://img.freepik.com/premium-vector/caduceus-medical-care-icon-vector_23-2147500971.jpg" 
            alt="Logo" 
            width={200} 
            height={200} 
            className="" 
          />
        </div>
        <h1 className="text-4xl font-extrabold text-white">Medicine Information</h1>
        <p className="text-lg mt-2 text-gray-100">By Dr Ashfa Shakeel</p>
        <p className="text-lg mt-2 text-gray-100">Homework By Sir Hamzah Syed</p>
        <p className="text-lg mt-2 text-gray-100">"A dose of knowledge for your health journey."</p>
        
      </header>

      <main className="p-6 space-y-10">
        {/* Introduction Section */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">What is Medicine?</h2>
          <p className="text-gray-700 text-2xl">
            Medicine is the science of diagnosing, treating, and preventing diseases. It plays a critical role in improving health and quality of life.
          </p>
        </section>

        {/* History of Medicine */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">History of Medicine</h2>
          <p className="text-gray-700  text-2xl">
            The practice of medicine has evolved over thousands of years, from ancient herbal remedies to the highly advanced, evidence-based treatments of today. Civilizations like the Egyptians, Greeks, and Chinese have all contributed to the advancement of medical knowledge. The development of antibiotics, vaccines, and surgical techniques has drastically improved healthcare outcomes.
          </p>
          <div className="mt-4">
            <Image 
              src="data:image/jpeg;base64," 
              alt="History of Medicine" 
              width={300} 
              height={200} 
              className="rounded-lg shadow-md" 
            />
          </div>
        </section>

        {/* Types of Medicine Section */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">Types of Medicine</h2>
          <div className="flex space-x-4 justify-center">
            <div className="p-4 bg-white shadow-lg rounded-lg transform transition duration-500 hover:scale-110 hover:rotate-3 hover:shadow-2xl">
              <Image
                src="data:image/jpeg;base64,"
                alt="Allopathic Medicine" 
                width={300} 
                height={300} 
                className="rounded-lg" 
              />
              <p className="text-center mt-2 text-gray-600">Allopathic</p>
            </div>
            <div className="p-4 bg-white shadow-lg rounded-lg transform transition duration-500 hover:scale-110 hover:rotate-3 hover:shadow-2xl">
              <Image
                src="data:image/jpeg;base64,"
                alt="Homeopathic Medicine" 
                width={300} 
                height={300} 
                className="rounded-lg" 
              />
              <p className="text-center mt-2 text-gray-600">Homeopathic</p>
            </div>
            <div className="p-4 bg-white shadow-lg rounded-lg transform transition duration-500 hover:scale-110 hover:rotate-3 hover:shadow-2xl">
              <Image
                src="data:image/jpeg;base64,"
                alt="Ayurvedic Medicine" 
                width={300} 
                height={300} 
                className="rounded-lg" 
              />
              <p className="text-center mt-2 text-gray-600">Ayurvedic</p>
            </div>
            <div className="p-4 bg-white shadow-lg rounded-lg transform transition duration-500 hover:scale-110 hover:rotate-3 hover:shadow-2xl">
              <Image
                src="data:image/jpeg;base64,"
                alt="Chinese Traditional Medicine" 
                width={300} 
                height={300} 
                className="rounded-lg" 
              />
              <p className="text-center mt-2 text-gray-600">Chinese Traditional</p>
            </div>
          </div>
        </section>

        {/* Common Drugs Section */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">Common Drugs</h2>
          <div className="overflow-hidden relative ">
            <div className="flex space-x-8 animate-scroll whitespace-nowrap">
              {['Ibuprofen', 'Amoxicillin', 'Lisinopril', 'Metformin', 'Atorvastatin'].map((drug, index) => (
                <div
                  key={index}
                  className="p-4 bg-gradient-to-r from-purple-400 to-pink-400 rounded-lg shadow-md text-white text-center transform transition duration-500 hover:scale-105">
                  <h3 className=" font-semibold text-2xl">{drug}</h3>
                  <p className=" mt-2 text-2xl">Use: {getDrugUse(drug)}</p>
                  <p className="text-2xl">Side Effects: {getDrugSideEffects(drug)}</p>
                </div>
              ))}
            </div>
          </div>
        </section>

        {/* Future of Medicine */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">The Future of Medicine</h2>
          <p className="text-gray-700 text-2xl">
            Medicine is rapidly evolving with advancements in technology. The future of medicine will likely involve personalized medicine, where treatments are tailored to an individual’s genetic makeup. Artificial Intelligence (AI) is also expected to revolutionize diagnostics and patient care, while biotechnology and robotics will open new frontiers in surgery and treatment.
          </p>
          <div className="mt-4">
            <Image 
              src="data:image/jpeg;base64," 
              alt="Future of Medicine" 
              width={300} 
              height={200} 
              className="rounded-lg shadow-md" 
            />
          </div>
        </section>

        {/* How to Choose the Right Medicine */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">How to Choose the Right Medicine</h2>
          <ul className="list-disc list-inside text-gray-700 text-2xl">
            <li>Consult with a licensed healthcare professional for diagnosis.</li>
            <li>Understand the side effects and interactions with other medications.</li>
            <li>Consider alternative treatments like herbal or homeopathic medicines.</li>
            <li>Always follow the recommended dosage instructions.</li>
            <li>Keep track of how your body responds to medication and report any adverse effects to your doctor.</li>
          </ul>
        </section>

        {/* Uses of Medicine Section */}
        <section>
          <h2 className="text-3xl font-bold text-blue-700 mb-4">Uses of Medicine</h2>
          <div className="grid grid-cols-2 gap-4">
            <Image 
              src="data:image/jpeg;base64," 
              alt="Uses of Medicine" 
              width={300} 
              height={200} 
              className="rounded-lg shadow-md"
            />
            <ul className="list-disc list-inside text-gray-700 text-2xl">
              <li>Treating diseases</li>
              <li>Symptom management</li>
              <li>Preventing infections</li>
              <li>Chronic condition management</li>
              <li>Saving lives in emergencies</li>
              <li>Right medicine to right person at right time</li>
            </ul>
          </div>
        </section>
      </main>

      <footer className="bg-gray-800 text-white text-center p-4">
        <p>&copy; 2024 Dr Ashfa Shakeel. All rights reserved.</p>
        <p>"Medicine is the best remedy, but understanding it is even better!"</p>
      </footer>
    </div>
  );
};

// Helper functions for drug use and side effects
const getDrugUse = (drug: string) => {
  switch (drug) {
    case 'Ibuprofen':
      return 'Pain relief, anti-inflammatory';
    case 'Amoxicillin':
      return 'Treats bacterial infections';
    case 'Lisinopril':
      return 'Treats high blood pressure';
    case 'Metformin':
      return 'Manages diabetes';
    case 'Atorvastatin':
      return 'Lowers cholesterol';
    default:
      return '';
  }
};

const getDrugSideEffects = (drug: string) => {
  switch (drug) {
    case 'Ibuprofen':
      return 'Nausea, headache, dizziness';
    case 'Amoxicillin':
      return 'Rash, nausea, allergic reactions';
    case 'Lisinopril':
      return 'Cough, dizziness, high potassium';
    case 'Metformin':
      return 'Nausea, diarrhea';
    case 'Atorvastatin':
      return 'Muscle pain, liver enzyme changes';
    default:
      return '';
  }
};

export default Home;
